    flexDirection: "row",
  },

  decreaseorder: {
    width: 50,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
    borderTopLeftRadius: 25,
    borderBottomLeftRadius: 25,
  },

  orderquantity: {
    width: 50,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
  },

  increaseorder: {
    width: 50,
    backgroundColor: "#fff",
    alignItems: "center",
    justifyContent: "center",
    borderTopRightRadius: 25,
    borderBottomRightRadius: 25,
  },
});
